/**
 * @description
 * An interface representing available style options for the
 * showValidation Directive
 * {@link ShowValidationDirective}
 */
export interface ShowValidationStyle {
    font_size?: string;
    font_family?: string;
    color?: string;
    background_color?: string;
    border?: string;
    border_radius?: string;
    width?: string;
    height?: string;
}
